package com.controller;

import java.io.IOException;
import java.io.PrintWriter;
import java.util.ArrayList;
import java.util.List;

import javax.servlet.ServletException;
import javax.servlet.annotation.WebServlet;
import javax.servlet.http.HttpServlet;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import javax.servlet.http.HttpSession;

import com.bean.Product;

/**
 * Servlet implementation class ProductController
 */
@WebServlet("/ProductController")
public class ProductController extends HttpServlet {
	private static final long serialVersionUID = 1L;
       
   
    public ProductController() {
        super();
        // TODO Auto-generated constructor stub
    }

	
	protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
	PrintWriter pw = response.getWriter();
	
	Product p1 = new Product();
	p1.setPid(100);
	p1.setPname("TV");
	p1.setPrice(56000);
	p1.setQty(10);
	p1.setUrl("data:image/jpeg;base64,");
	
	Product p2 = new Product();
	p2.setPid(101);
	p2.setPname("Computer");
	p2.setPrice(45000);
	p2.setQty(16);
	p2.setUrl("data:image/png;base64,");
	
	Product p3 = new Product();
	p3.setPid(102);
	p3.setPname("Laptop");
	p3.setPrice(86000);
	p3.setQty(50);
	p3.setUrl("data:image/webp;base64,");
	
	Product p4 = new Product();
	p4.setPid(103);
	p4.setPname("Pen Drive");
	p4.setPrice(2600);
	p4.setQty(100);
	p4.setUrl("data:image/jpeg;base64,");
	
	List<Product> listOfProduct = new ArrayList<Product>();
	listOfProduct.add(p1);
	listOfProduct.add(p2);
	listOfProduct.add(p3);
	listOfProduct.add(p4);
	
	HttpSession hs = request.getSession();
	hs.setAttribute("products", listOfProduct);
	
	response.sendRedirect("displayProduct.jsp");
	}

	

}

